package java_Tutorial;

public class Time {

	int houre;
	int minute;
	int second;
	
	public Time(int houre, int minute, int second) {
		// TODO Auto-generated constructor stub
	}
	
	public int getHoure() {
		return houre;
	}
	
	public int getMinute() {
		return minute;
	}
	
	public int getSecond() {
		return second;
	}
	
	public void setHoure(int houre) {
		this.houre = houre;
	}
	
	public void setMinute(int minute) {
		this.minute = minute;
	}
	
	public void setSecond(int second) {
		this.second = second;
	}
	
}
